def vertex_creator(task):
    v_dict = {v:task.index(v) for v in task}
    return v_dict

def edge_creator(c_dict,task):
    v_dict = vertex_creator(task)
    non_zero_keys = list(filter(lambda key: True if c_dict[key][0]>0 else False,task))
    non_zero_keys_num = list(map(lambda key:v_dict[key],non_zero_keys))#[v_dict[key] for key in non_zero_keys]
    comb = list(combinations(non_zero_keys,2))
    #edges = [(v_dict[non_zero_keys[i]],v_dict[non_zero_keys[i+1]]) for i in range(len(non_zero_keys)-1)]
    return comb

